import React, { useState } from 'react';

function InputDOB({ dob, setDob, setUsername, onNext }) {
    const [error, setError] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!dob) {
            setError('Please enter a valid DOB.');
            return;
        }

        // Simulate fetching username based on DOB
        const fakeUsername = 'John Doe'; // Replace this with actual fetch logic
        setUsername(fakeUsername);
        setError('');
        onNext(); // Move to the next step
    };

    return (
        <div>
            <h1>Enter Your Date of Birth</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type="date"
                    value={dob}
                    onChange={(e) => setDob(e.target.value)}
                    style={{ padding: '10px', fontSize: '16px' }}
                />
                <button type="submit" style={{ padding: '10px', marginLeft: '10px' }}>
                    Submit
                </button>
            </form>
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </div>
    );
}

export default InputDOB;
