import React, { useState } from 'react';

function BlowCandle() {
    const [candlesBlown, setCandlesBlown] = useState(false);

    const handleBlow = () => setCandlesBlown(true);

    return (
        <div>
            <h1>ðŸŽ‰ Make a Wish and Blow the Candles! ðŸŽ‰</h1>
            <div className="candle-container">
                {!candlesBlown ? (
                    <img 
                        src="path_to_lit_candles_image_or_animation.gif" 
                        alt="Lit Candles" 
                        style={{ width: '300px', height: '300px' }}
                    />
                ) : (
                    <img 
                        src="path_to_blow_out_candles_image_or_animation.gif" 
                        alt="Blown Out Candles" 
                        style={{ width: '300px', height: '300px' }}
                    />
                )}
            </div>
            {!candlesBlown && (
                <button onClick={handleBlow}>Blow the Candles!</button>
            )}
        </div>
    );
}

export default BlowCandle;
