import React, { useEffect } from 'react';

const BirthdayCake = ({ onAnimationEnd }) => {
    useEffect(() => {
        // Assuming birthdayCakeAnimation is a function that triggers the animation
        birthdayCakeAnimation();  // This function handles both the cake and blow-out candles animation

        // Notify the parent component when the animation ends
        setTimeout(() => {
            onAnimationEnd();  // Callback to trigger the next step after animation
        }, 10000);  // Adjust based on the duration of the animation
    }, []);

    return (
        <div id="birthday-cake">
            {/* The birthday cake animation is triggered here */}
        </div>
    );
};

export default BirthdayCake;
