import React, { useState } from 'react';
import { animated, useSpring } from 'react-spring';

const BlowCandle = () => {
    const [blown, setBlown] = useState(false);

    const flameStyle = useSpring({
        opacity: blown ? 0 : 1,
        transform: blown ? 'scale(0)' : 'scale(1)',
        config: { duration: 500 },
    });

    const blowOutCandle = () => {
        setBlown(true);
        setTimeout(() => alert('Make a wish! ðŸŽ‚'), 1000);
    };

    return (
        <div style={{ textAlign: 'center', marginTop: '20px' }}>
            <div
                style={{
                    width: '50px',
                    height: '100px',
                    backgroundColor: '#FFDDC1',
                    margin: 'auto',
                    position: 'relative',
                    borderRadius: '10px',
                }}
            >
                <animated.div
                    style={{
                        ...flameStyle,
                        width: '20px',
                        height: '40px',
                        backgroundColor: '#FFA500',
                        borderRadius: '50%',
                        position: 'absolute',
                        top: '-40px',
                        left: '15px',
                    }}
                />
            </div>
            <button
                onClick={blowOutCandle}
                style={{
                    marginTop: '20px',
                    padding: '10px 20px',
                    backgroundColor: '#ff4081',
                    color: '#fff',
                    border: 'none',
                    borderRadius: '5px',
                    cursor: 'pointer',
                }}
            >
                Blow the Candle
            </button>
        </div>
    );
};

export default BlowCandle;
