import React, { useState, useEffect } from 'react';
import InputDOB from './InputDOB';
import Wishes from './Wishes';
import BirthdayCake from './components/BirthdayCake';

const App = () => {
  const [dob, setDob] = useState('');
  const [username, setUsername] = useState('');
  const [wishes, setWishes] = useState([]);
  const [currentWish, setCurrentWish] = useState('');
  const [screen, setScreen] = useState('input'); // 'input', 'happyBirthday', 'wishes', 'cake'

  useEffect(() => {
    if (wishes.length > 0) {
      let i = 0;
      const wishInterval = setInterval(() => {
        if (i < wishes.length) {
          setCurrentWish(wishes[i]);
          i++;
        } else {
          clearInterval(wishInterval);
          setTimeout(() => setScreen('cake'), 5000); // Transition to cake after wishes
        }
      }, 5000);
      return () => clearInterval(wishInterval);
    }
  }, [wishes]);

  const fetchData = async () => {
    if (!dob) return;
    try {
      const response = await fetch(`/users/dob/${dob}`);
      if (!response.ok) throw new Error('Failed to fetch data');
      const data = await response.json();
      setUsername(data.username);
      setWishes(data.wishes);
      setScreen('happyBirthday');
      setTimeout(() => setScreen('wishes'), 5000); // Transition after 5 seconds
    } catch (error) {
      console.error('Error fetching user data:', error);
    }
  };

  return (
    <div>
      {screen === 'input' && <InputDOB dob={dob} setDob={setDob} fetchData={fetchData} />}
      {screen === 'happyBirthday' && <h1>Happy Birthday, {username}!</h1>}
      {screen === 'wishes' && <Wishes currentWish={currentWish} />}
      {screen === 'cake' && <BirthdayCake />}
    </div>
  );
};

export default App;
