import React, { useState, useEffect } from 'react';
import InputDOB from './components/inputDOB';
import Wishes from './components/Wishes';
import BirthdayCake from './components/BirthdayCake';

function App() {
    const [step, setStep] = useState(1); // Track the current step
    const [dob, setDob] = useState(''); // Store the entered DOB

    // Function to handle moving to the next step
    const handleNextStep = () => {
      if (step === 1 && dob) {
        setStep(2); // Move to the Wishes step
      } else if (step === 2) {
        setStep(3); // Move to the Birthday Cake step
      }
    };

    return (
      <div>
        {step === 1 && <InputDOB dob={dob} setDob={setDob} handleNextStep={handleNextStep} />}
        {step === 2 && <Wishes handleNextStep={handleNextStep} />}
        {step === 3 && <BirthdayCake />}
      </div>
    );
  }

  export default App;
